mapper.select:

SELECT rnum, answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
FROM (
  SELECT rownum, answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
  FROM (
      SELECT answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
      FROM interview_answers
      ORDER BY created_at DESC
    )
  WHERE rownum < #{endRowNo} 
  )
WHERE rnum > #{startRowNo}


SELECT rnum, answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
FROM (
  SELECT rownum, answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
  FROM (
      SELECT answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
      FROM interview_answers
      WHERE session_id = #{sessionId}
      ORDER BY created_at DESC
    )
  WHERE rownum < #{endRowNo} 
  )
WHERE rnum > #{startRowNo}

SELECT answer_id, question_id, session_id, answer_text, ai_feedback, ai_score, created_at
FROM interview_answers
WHERE answer_id = #{answerId}
