mapper.select:

SELECT 
rnum, session_id, user_id, type, target_job, target_company, report_summary, feedback_vector, created_at
FROM (
  SELECT rownum, session_id, user_id, type, target_job, target_company, report_summary, feedback_vector, created_at
  FROM (
      SELECT session_id, user_id, type, target_job, target_company, report_summary, feedback_vector, created_at
      FROM interview_sessions
      ORDER BY created_at DESC
    )
  WHERE rownum < #{endRowNo} 
  )
WHERE rnum > #{startRowNo}

mapper.insert:
INSERT INTO interview_sessions (
  session_id, user_id, type, target_job, target_company, 
  report_summary, feedback_vector, created_at
) VALUES (
  #{sessionId}, #{userId}, #{type}, #{targetJob}, #{targetCompany}, 
  #{reportSummary}, #{feedbackVector}, #{createdAt}
)
