mapper.insert:
INSERT INTO interview_sessions (
  session_id, user_id, type, target_job, target_company, 
  report_feedback, created_at
) VALUES (
  #{sessionId}, #{userId}, #{type}, #{targetJob}, #{targetCompany}, 
  #{reportFeedback}, #{createdAt}
)

mapper.update:
UPDATE interview_sessions
SET report_feedback = CAST(#{reportFeedback, jdbcType=OTHER} AS JSONB)
WHERE session_id = #{sessionId}
